import React from 'react'
import { Component } from 'react/cjs/react.production.min'
import './App.css'

function UploadForm() {
    return (
        <div className='UploadForm'>
            <h1>Batch Processing:</h1>
            <FormBatch />
        </div>
    )
}

export default UploadForm

class FormBatch extends Component{
    constructor(props){
        super(props)
        this.state= {
            selectFile: null,
            respFromServer:null
        }

        this.handleFile = this.handleFile.bind(this)
        this.handleUpload = this.handleUpload.bind(this)
    }

    handleFile(event){
        this.setState({[event.target.name]: event.target.value})
    }

    handleUpload(event){
        event.preventDefault()
        const url="http://127.0.0.1:8000/scoreFile"
        const fileToSend= this.state.selectFile
        var formdata = new FormData();
        formdata.append("filePath", this.state.selectFile, this.selectFile.name)
        const reqOpt={
            method:'POST', body: formdata
        }
        
    }

    render(){
        return(
            <div>
                <form onSubmit={this.handleUpload}>
                    <input type='file' name='selectFile' onChange={this.handleFile}></input>
                    <input type='submit' value='Submit'></input>
                </form>
                <div>{ this.state.selectFile}</div>
            </div>
        )
    }
}
