import React from 'react'
import { Component } from 'react'
import './App.css'

function Home() {
    return (
        <div className='Home'>
            <h2>Enter details to get approval chance of your application</h2>
            <JsonForm />
        </div>
    )
}

export default Home

class JsonForm extends Component{
    constructor(props){
        super(props)
        this.state={Gender:'Male',Married:'No',
        Dependents:0,Education:'Graduate',Self_Employed: 'No',
        Applicant_Income:5489,	Coapplicant_Income:0,	Loan_Amount:128,	Loan_Amount_Term:360,	Credit_History:1,	Property_Area:'Urban',
        score:1}

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event){
        this.setState({
            [event.target.name] : [event.target.value]
        })
    }

    handleSubmit(event){
        event.preventDefault();
        this.setState({score :10})
        const url= "http://127.0.0.1:8000/scoreJson"

        const bodyData=JSON.stringify({
            "Geneder": this.state.Gender, "Married": this.state.Married,
            "Dependents": this.state.Dependents, "Education" : this.state.Education,
            "Self_Employed": this.state.Self_Employed, "Applicant_Income": this.stateApplicant_Income,
            "Coapplicant_Income": this.state.Coapplicant_Income, "Loan_Amount": this.state.Loan_Amount,
            "Loan_Amount_Term": this.state.Loan_Amount_Term, "Credit_History": this.state.Credit_History, "Property_Area": this.state.Property_Area
        })

        const reqOpt= {method:"POST", 
                       header:{"Content-type":"application/json"},
                       body: bodyData}

        fetch(url, reqOpt)
        .then((resp) => resp.json)
        .then((respJ) => this.setState({score: respJ.score}))
    }

    render(){
        return(
            <div>
                <form onSubmit={this.handleSubmit}>
                    <table>
                        <tbody>
                            <tr><td>Gender :</td>
                            <td><input type="text" value={this.state.Gender} name='Gender' onChange={this.handleChange}></input></td></tr>
                            <tr><td>Married: </td>
                            <td><input type="text" value={this.state.Married} name='Married' onChange={this.handleChange}></input></td></tr>
                            <tr><td>Dependents :</td>
                            <td><input type="text" value={this.state.Dependents} name='Dependents' onChange={this.handleChange}></input></td></tr>
                            <tr><td>Education :</td>
                            <td><input type="text" value={this.state.Education} name='Education' onChange={this.handleChange}></input></td></tr>
                            <tr><td>Self_Employed :</td>
                            <td><input type="text" value={this.state.Self_Employed} name='Self_Employed' onChange={this.handleChange}></input></td></tr>
                            <tr><td>Applicant_Income :</td>
                            <td><input type="text" value={this.state.Applicant_Income} name='Applicant_Income' onChange={this.handleChange}></input></td></tr>
                            <tr><td>Coapplicant_Income :</td>
                            <td><input type="text" value={this.state.Coapplicant_Income} name='Coapplicant_Income' onChange={this.handleChange}></input></td></tr>
                            <tr><td>Loan_Amount :</td>
                            <td><input type="text" value={this.state.Loan_Amount} name='Loan_Amount' onChange={this.handleChange}></input></td></tr>
                            <tr><td>Loan_Amount_Term :</td>
                            <td><input type="text" value={this.state.Loan_Amount_Term} name='Loan_Amount_Term' onChange={this.handleChange}></input></td></tr>
                            <tr><td>Credit_History :</td>
                            <td><input type="text" value={this.state.Credit_History} name='Credit_History' onChange={this.handleChange}></input></td></tr>
                            <tr><td>Property_Area :</td>
                            <td><input type="text" value={this.state.Property_Area} name='Property_Area' onChange={this.handleChange}></input></td></tr>
                            

                        </tbody>
                    </table>
                    <input type="submit" value="Submit"></input>
                </form>
                <div><h3>The probability of getting approval is {this.state.score}</h3></div>
            </div>
        );
    }
}
